mapper.insert:
INSERT INTO NOTICE( nNUM, nTITLE, nSUBTITLE, nCNT, nCOMMENT
, nNAME , nREALNAME, nNSIZE, nMAINNAME, nMAINREALNAME, nMAINNSIZE
,nDATE ,nTAG1 ,nTAG2 ,nTAG3 ) VALUES( nextval(howstudy.nNUM), #{nTITLE}, #{nSUBTITLE}, 0, #{nCOMMENT}
,#{nNAME},#{nREALNAME},#{nNSIZE},#{nMAINNAME},#{nMAINREALNAME},#{nMAINNSIZE}
,NOW() ,#{nTAG1} ,#{nTAG2} ,#{nTAG3} )

mapper.update:
        update NOTICE set
            nTITLE = #{nTITLE}
            , nSUBTITLE = #{nSUBTITLE}
            , nCOMMENT = #{nCOMMENT}

			, nNAME = #{nNAME}
			, nREALNAME = #{nREALNAME}
			, nNSIZE = #{nNSIZE}
			, nMAINNAME = #{nMAINNAME}
			, nMAINREALNAME = #{nMAINREALNAME}
			, nMAINNSIZE = #{nMAINNSIZE}

            , nTAG1 = #{nTAG1}
            , nTAG2 = #{nTAG2}
            , nTAG3 = #{nTAG3}
        where
            nNUM = #{nNUM}
