mapper.insert:
INSERT into SaveTime (saveNum, sTime , mID, sNUM, sCATEGORY, sDate )(
select nextval(sTimeNum),date_format(CONCAT(CURDATE(),'
',#{sTime}),'%H:%i:%s'
),#{mId}, s.sNUM, s.sCATEGORY,SYSDATE()
from STUDYROOM s where s.sNum = #{sNum})

mapper.select:
select sum(TIME_TO_SEC( sTime)) sTime, sCATEGORY sCATEGORY from SaveTime st
where st.mID = #{mId}
group by sCATEGORY;
